import{d as C}from"./app.fce06a0c.js";import{_ as p}from"./plugin-vue_export-helper.21dcd24c.js";const e={},a=C('<h1 id="_1-3-c-\u7684\u8BBE\u8BA1" tabindex="-1"><a class="header-anchor" href="#_1-3-c-\u7684\u8BBE\u8BA1" aria-hidden="true">#</a> 1.3 C++ \u7684\u8BBE\u8BA1</h1><p>\u7B80\u5355\u6027\u662F\u4E00\u4E2A\u91CD\u8981\u8BBE\u8BA1\u51C6\u5219\uFF1A\u5982\u679C\u5728\u67D0\u4E2A\u5730\u65B9\u6709\u4E00\u4E2A\u9009\u62E9\uFF0C\u7B80\u5316\u8BED\u8A00\u7684\u5B9A\u4E49\u6216\u8005\u7B80\u5316\u7F16\u8BD1\u5668\uFF0C\u90A3\u4E48\u6211\u4EEC\u4E00\u5B9A\u9009\u524D\u8005\u3002\u5F53\u7136\uFF0C\u8FD8\u6709\u4E00\u4E2A\u91CD\u8981\u7684\u8003\u8651\u662F\u4FDD\u6301\u4E0EC\u7684\u9AD8\u5EA6\u517C\u5BB9\u6027\uFF0C\u8FD9\u4E5F\u5C31\u6392\u9664\u4E86\u5BF9C\u8BED\u6CD5\u7684\u6E05\u7406\u3002</p><p>C++\u6CA1\u6709\u5185\u90E8\u7684\u9AD8\u7EA7\u6570\u636E\u7C7B\u578B\uFF0C\u4E5F\u6CA1\u6709\u9AD8\u7EA7\u7684\u57FA\u672C\u64CD\u4F5C\u3002\u4E3E\u4F8B\u6765\u8BF4\uFF0CC++\u6CA1\u6709\u63D0\u4F9B\u5E26\u6709\u6C42\u9006\u8FD0\u7B97\u7684\u77E9\u9635\u7C7B\u578B\uFF0C\u4E5F\u6CA1\u6709\u5E26\u62FC\u63A5\u8FD0\u7B97\u7684\u5B57\u7B26\u4E32\u7C7B\u578B\u3002\u5982\u679C\u67D0\u4E2A\u7528\u6237\u9700\u8981\u4E00\u4E2A\u7C7B\u578B\uFF0C\u90A3\u4E48\u53EF\u4EE5\u5728\u8BED\u8A00\u672C\u8EAB\u4E4B\u4E2D\u5B9A\u4E49\u5B83\u3002\u4E8B\u5B9E\u4E0A\uFF0C\u5B9A\u4E49\u65B0\u7684\u901A\u7528\u6216\u8005\u4E13\u7528\u7C7B\u578B\u5C31\u662F\u5728C++\u91CC\u6700\u57FA\u672C\u7684\u7A0B\u5E8F\u8BBE\u8BA1\u6D3B\u52A8\u3002\u4E00\u4E2A\u8BBE\u8BA1\u826F\u597D\u7684\u7528\u6237\u5B9A\u4E49\u7C7B\u578B\u4E0E\u4E00\u4E2A\u5185\u90E8\u7C7B\u578B\u4E4B\u95F4\u7684\u5DEE\u5F02\u4EC5\u4EC5\u5728\u4E8E\u5176\u5B9A\u4E49\u7684\u65B9\u5F0F\uFF0C\u800C\u4E0D\u5728\u5176\u4F7F\u7528\u65B9\u5F0F\u3002\u5728\u7B2C\u4E09\u90E8\u5206\u63CF\u8FF0\u7684\u6807\u51C6\u5E93\u63D0\u4F9B\u4E86\u8BB8\u591A\u8FD9\u6837\u7684\u7C7B\u578B\u53CA\u5176\u4F7F\u7528\u7684\u4F8B\u5B50\u3002\u4ECE\u7528\u6237\u7684\u89C2\u70B9\u770B\uFF0C\u5728\u5185\u90E8\u7C7B\u578B\u548C\u7531\u6807\u51C6\u5E93\u63D0\u4F9B\u7684\u7C7B\u578B\u4E4B\u95F4\u7684\u5DEE\u5F02\u975E\u5E38\u5C0F\u3002</p><p>\u5728C++\u7684\u8BBE\u8BA1\u4E2D\uFF0C\u6781\u529B\u907F\u514D\u4E86\u90A3\u4E9B\u5373\u4F7F\u4E0D\u7528\u4E5F\u4F1A\u5E26\u6765\u8FD0\u884C\u65F6\u95F4\u6216\u8005\u7A7A\u95F4\u989D\u5916\u5F00\u9500\u7684\u7279\u5F81\u3002\u4F8B\u5982\uFF0C\u8981\u6C42\u5FC5\u987B\u5728\u6BCF\u4E2A\u5BF9\u8C61\u91CC\u5B58\u50A8\u67D0\u79CD\u201C\u8584\u8BB0\u4FE1\u606F\u201D\u7684\u7ED3\u6784\u88AB\u62D2\u7EDD\u4E86\u3002\u6240\u4EE5\uFF0C\u5982\u679C\u4F60\u5B9A\u4E49\u4E86\u4E00\u79CD\u7531\u4E24\u4E2A16\u4F4D\u7684\u91CF\u7EC4\u6210\u7684\u7ED3\u6784\uFF0C\u5B83\u5C06\u80FD\u653E\u8FDB\u4E00\u4E2A32\u4F4D\u7684\u5BC4\u5B58\u5668\u91CC\u3002</p><p>C++\u88AB\u8BBE\u8BA1\u4E3A\u80FD\u4F7F\u7528\u4F20\u7EDF\u7684\u7F16\u8BD1\u548C\u8FD0\u884C\u65F6\u7684\u73AF\u5883\uFF0C\u4E5F\u5C31\u662F\u90A3\u79CD\u5728UNIX\u4E0A\u7684C\u7A0B\u5E8F\u8BBE\u8BA1\u73AF\u5883\u3002\u5E78\u8FD0\u7684\u662F\uFF0CC++\u4ECE\u6765\u6CA1\u6709\u88AB\u675F\u7F1A\u4E8EUNIX\uFF0C\u5B83\u53EA\u662F\u7B80\u5355\u5730\u91C7\u7528UNIX\u548CC\u4F5C\u4E3A\u4E00\u79CD\u8BED\u8A00\u3001\u5E93\u3001\u7F16\u8BD1\u5668\u3001\u8FDE\u63A5\u5668\u3001\u6267\u884C\u73AF\u5883\u7B49\u4E4B\u95F4\u5173\u7CFB\u7684\u6A21\u578B\u3002\u8FD9\u79CD\u6700\u5C0F\u5316\u7684\u6A21\u578B\u5E2E\u52A9C++\u5728\u51E0\u4E4E\u6BCF\u4E2A\u8BA1\u7B97\u5E73\u53F0\u4E0A\u53D6\u5F97\u4E86\u6210\u529F\u3002\u7136\u800C\uFF0C\u5728\u90A3\u4E9B\u63D0\u4F9B\u4E86\u66F4\u591A\u6709\u610F\u4E49\u7684\u652F\u6301\u7684\u73AF\u5883\u91CC\uFF0C\u4F7F\u7528C++\u5F53\u7136\u5C31\u66F4\u597D\u4E86\uFF0C\u50CF\u52A8\u6001\u88C5\u8F7D\u3001\u589E\u91CF\u7F16\u8BD1\u3001\u7C7B\u578B\u5B9A\u4E49\u6570\u636E\u5E93\u7B49\u90FD\u80FD\u5F97\u5230\u5F88\u597D\u7684\u6536\u655B\uFF0C\u53C8\u4E0D\u4F1A\u5F71\u54CD\u8BED\u8A00\u672C\u8EAB\u3002</p><p>C++\u7684\u7C7B\u578B\u68C0\u67E5\u548C\u6570\u636E\u9690\u85CF\u7279\u5F81\u4F9D\u8D56\u4E8E\u7F16\u8BD1\u65F6\u5BF9\u7A0B\u5E8F\u7684\u5206\u6790\uFF0C\u4EE5\u9632\u6B62\u56E0\u4E3A\u610F\u5916\u800C\u7834\u574F\u6570\u636E\u7684\u60C5\u51B5\u3002\u5B83\u4EEC\u5E76\u4E0D\u63D0\u4F9B\u7CFB\u7EDF\u5B89\u5168\u6027\u6216\u9632\u6B62\u67D0\u4E9B\u4EBA\u6709\u610F\u5730\u6253\u7834\u8FD9\u4E9B\u89C4\u5219\u3002\u5B83\u4EEC\u5F53\u7136\u53EF\u4EE5\u968F\u610F\u4F7F\u7528\u800C\u4E0D\u4F1A\u5E26\u6765\u8FD0\u884C\u65F6\u989D\u5916\u7684\u65F6\u95F4\u6216\u7A7A\u95F4\u5F00\u9500\u3002\u8FD9\u79CD\u60F3\u6CD5\u662F\u5F88\u6709\u7528\u7684\uFF0C\u4E00\u79CD\u8BED\u8A00\u7279\u5F81\u5FC5\u987B\u4E0D\u4EC5\u662F\u4F18\u7F8E\u7684\uFF0C\u8FD8\u662F\u5728\u771F\u5B9E\u7A0B\u5E8F\u7684\u73AF\u5883\u4E2D\u80FD\u591F\u8D1F\u62C5\u8D77\u7684\u4E1C\u897F\u3002</p><p>\u6709\u5173C++\u8BBE\u8BA1\u7684\u66F4\u7CFB\u7EDF\u548C\u8BE6\u5C3D\u7684\u63CF\u8FF0\uFF0C\u8BF7\u770B[Stroustrup\uFF0C1994]\u3002</p><h2 id="_1-3-1-\u6548\u7387\u548C\u7ED3\u6784" tabindex="-1"><a class="header-anchor" href="#_1-3-1-\u6548\u7387\u548C\u7ED3\u6784" aria-hidden="true">#</a> 1.3.1 \u6548\u7387\u548C\u7ED3\u6784</h2><p>C++\u662F\u4EE5C\u7A0B\u5E8F\u8BBE\u8BA1\u8BED\u8A00\u4E3A\u57FA\u7840\u5F00\u53D1\u51FA\u6765\u7684\uFF0C\u9664\u4E86\u5C11\u91CF\u4F8B\u5916\uFF0C\u5B83\u7EE7\u7EED\u7EF4\u6301\u4E86\u4EE5C\u4F5C\u4E3A\u4E00\u4E2A\u5B50\u96C6\u3002\u4F5C\u4E3A\u57FA\u7840\u8BED\u8A00\uFF0CC++\u7684C\u5B50\u96C6\u8BBE\u8BA1\u4FDD\u8BC1\u4E86\u5728\u5B83\u7684\u7C7B\u578B\u3001\u8FD0\u7B97\u3001\u8BED\u53E5\u4E0E\u8BA1\u7B97\u673A\u76F4\u63A5\u5904\u7406\u7684\u5BF9\u8C61\uFF08\u6570\u3001\u5B57\u7B26\u548C\u5730\u5740\uFF09\u4E4B\u95F4\u7684\u7D27\u5BC6\u5BF9\u5E94\u5173\u7CFB\u3002\u9664\u4E86new\u3001delete\u3001dynamic_cast\uFF0C\u4EE5\u53CAthrow\u8FD0\u7B97\u7B26\u548Ctry-\u5757\u4E4B\u5916\uFF0CC++\u7684\u5404\u79CD\u8868\u8FBE\u5F0F\u548C\u8BED\u53E5\u90FD\u4E0D\u9700\u8981\u7279\u6B8A\u7684\u8FD0\u884C\u65F6\u652F\u6301\u3002</p><p>C++\u53EF\u4EE5\u4F7F\u7528\u4E0EC\u4E00\u6837\u7684\u51FD\u6570\u8C03\u7528\u53CA\u8FD4\u56DE\u5E8F\u5217---\u6216\u8005\u5176\u4ED6\u6548\u7387\u66F4\u9AD8\u7684\u65B9\u5F0F\u3002\u5728\u8FD9\u79CD\u76F8\u5BF9\u6709\u6548\u7684\u673A\u5236\u4ECD\u7136\u88AB\u8BA4\u4E3A\u662F\u4EE3\u4EF7\u8FC7\u9AD8\u4E4B\u5904\uFF0CC++\u51FD\u6570\u8FD8\u53EF\u4EE5\u91C7\u7528\u5728\u7EBF\u66FF\u6362\uFF0C\u8FD9\u6837\u5C31\u4F7F\u6211\u4EEC\u80FD\u5728\u4EAB\u53D7\u5230\u51FD\u6570\u8BB0\u6CD5\u4E0A\u65B9\u4FBF\u7684\u540C\u65F6\uFF0C\u53C8\u4E0D\u5FC5\u4ED8\u51FA\u4EFB\u4F55\u8FD0\u884C\u65F6\u7684\u5F00\u9500\u3002</p><p>C\u7684\u4E00\u4E2A\u521D\u59CB\u76EE\u6807\u662F\u5728\u5927\u90E8\u5206\u82DB\u523B\u7684\u7CFB\u7EDF\u7A0B\u5E8F\u8BBE\u8BA1\u5DE5\u4F5C\u4E2D\u4EE3\u66FF\u6C47\u7F16\u8BED\u8A00\u3002\u5728\u8BBE\u8BA1C++\u65F6\uFF0C\u4E5F\u7279\u522B\u6CE8\u610F\u4E86\u4E0D\u5728\u8FD9\u4E9B\u5DF2\u7ECF\u53D6\u5F97\u7684\u9886\u57DF\u4E2D\u505A\u51FA\u4EFB\u4F55\u59A5\u534F\u3002\u5728C\u548CC++\u4E4B\u95F4\u7684\u5DEE\u5F02\uFF0C\u4ECE\u6839\u672C\u4E0A\u8BF4\uFF0C\u5728\u4E8E\u5F3A\u8C03\u7C7B\u578B\u548C\u7ED3\u6784\u7684\u7EA7\u522B\u4E0D\u540C\u3002C\u662F\u6709\u8868\u8FBE\u80FD\u529B\u7684\u548C\u5BBD\u5BB9\u7684\uFF0C\u800CC++\u7684\u8868\u8FBE\u80FD\u529B\u66F4\u5F3A\u3002\u5F53\u7136\uFF0C\u4E3A\u4E86\u83B7\u5F97\u8FD9\u79CD\u589E\u5F3A\u7684\u8868\u8FBE\u80FD\u529B\uFF0C\u4F60\u5FC5\u987B\u66F4\u591A\u5730\u5173\u6CE8\u5BF9\u8C61\u7684\u7C7B\u578B\u3002\u77E5\u9053\u4E86\u5BF9\u8C61\u7684\u7C7B\u578B\uFF0C\u7F16\u8BD1\u5668\u5C31\u80FD\u6B63\u786E\u5904\u7406\u8868\u8FBE\u5F0F\uFF1B\u5982\u679C\u4E0D\u662F\u8FD9\u6837\uFF0C\u4F60\u5C31\u5FC5\u987B\u81EA\u5DF1\u5728\u4EE4\u4EBA\u96BE\u53D7\u7684\u7EC6\u8282\u7A0B\u5EA6\u4E0A\u53BB\u63CF\u8FF0\u6709\u5173\u64CD\u4F5C\u3002\u77E5\u9053\u4E86\u5BF9\u8C61\u7684\u7C7B\u578B\uFF0C\u7F16\u8BD1\u5668\u5C31\u80FD\u68C0\u67E5\u9519\u8BEF\uFF1B\u5426\u5219\u8FD9\u4E9B\u4E1C\u897F\u5C31\u4F1A\u9057\u7559\u4E0B\u6765\u76F4\u81F3\u6D4B\u8BD5\u9636\u6BB5---\u6216\u8BB8\u7559\u5230\u66F4\u665A\u7684\u65F6\u5019\u3002\u8BF7\u6CE8\u610F\uFF0C\u4F7F\u7528\u7C7B\u578B\u7CFB\u7EDF\u53BB\u68C0\u67E5\u51FD\u6570\u7684\u53C2\u6570\uFF0C\u53BB\u4FDD\u62A4\u6570\u636E\u4E0D\u88AB\u610F\u5916\u5730\u7834\u574F\uFF0C\u53BB\u63D0\u4F9B\u65B0\u7684\u7C7B\u578B\uFF0C\u53BB\u63D0\u4F9B\u65B0\u7684\u64CD\u4F5C\uFF0C\u5982\u6B64\u7B49\u7B49\u3002\u8FD9\u4E9B\u5728C++\u91CC\u90FD\u6CA1\u6709\u589E\u52A0\u4EFB\u4F55\u8FD0\u884C\u65F6\u7684\u65F6\u95F4\u6216\u8005\u7A7A\u95F4\u5F00\u9500\u3002</p><p>C++\u4E2D\u7279\u522B\u5F3A\u8C03\u7A0B\u5E8F\u7684\u7ED3\u6784\uFF0C\u8FD9\u53CD\u6620\u4E86\u81EAC\u8BBE\u8BA1\u4EE5\u6765\u7A0B\u5E8F\u89C4\u6A21\u7684\u589E\u957F\u60C5\u51B5\u3002\u4F60\u53EF\u4EE5\u901A\u8FC7\u73A9\u547D\u5E72\u505A\u51FA\u4E00\u4E2A\u5C0F\u7A0B\u5E8F\uFF08\u6BD4\u5982\u8BF4\uFF0C1000\u884C\uFF09\uFF0C\u751A\u81F3\u662F\u5728\u4F60\u8FDD\u53CD\u4E86\u6240\u6709\u6709\u5173\u597D\u98CE\u683C\u7684\u89C4\u5219\u7684\u60C5\u51B5\u4E0B\u3002\u800C\u5BF9\u4E8E\u66F4\u5927\u7684\u7A0B\u5E8F\uFF0C\u60C5\u51B5\u5C31\u5B8C\u5168\u4E0D\u540C\u4E86\u3002\u5982\u679C\u4E00\u4E2A100 000\u884C\u7684\u7A0B\u5E8F\u7684\u7ED3\u6784\u53CA\u5176\u7CDF\u7CD5\uFF0C\u4F60\u5C31\u4F1A\u53D1\u73B0\uFF0C\u5F15\u8FDB\u65B0\u9519\u8BEF\u7684\u901F\u5EA6\u50CF\u6E05\u9664\u8001\u9519\u8BEF\u4E00\u6837\u5FEB\u3002C++\u7684\u8BBE\u8BA1\u5C31\u662F\u4E3A\u4E86\u4F7F\u8F83\u5927\u7684\u7A0B\u5E8F\u80FD\u591F\u4EE5\u4E00\u79CD\u5408\u7406\u7684\u65B9\u5F0F\u6784\u9020\u51FA\u6765\uFF0C\u5E76\u56E0\u6B64\u4F7F\u4E00\u4E2A\u4EBA\u4E5F\u6709\u53EF\u80FD\u5BF9\u4ED8\u76F8\u5F53\u5927\u7684\u4E00\u6279\u4EE3\u7801\u3002\u8FDB\u4E00\u6B65\u7684\u76EE\u6807\u662F\u4F7F\u4E00\u4E2A\u5E73\u5747\u884C\u7684C++\u4EE3\u7801\u80FD\u591F\u8868\u8FF0\u51FA\u8FDC\u6BD4\u4E00\u4E2A\u5E73\u5747\u884C\u7684C\u6216Pascal\u4EE3\u7801\u66F4\u591A\u7684\u4E1C\u897F\u3002C++\u5DF2\u7ECF\u8BC1\u660E\u5B83\u8D85\u8FC7\u4E86\u8FD9\u4E9B\u76EE\u6807\u3002</p><p>\u5E76\u4E0D\u662F\u6BCF\u5757\u4EE3\u7801\u90FD\u53EF\u80FD\u662F\u7ED3\u6784\u826F\u597D\u7684\uFF0C\u4E0E\u786C\u4EF6\u65E0\u5173\u7684\uFF0C\u5BB9\u6613\u9605\u8BFB\u7684\uFF0C\u5982\u6B64\u7B49\u7B49\u3002C++\u4E5F\u62E5\u6709\u4E00\u4E9B\u7279\u5F81\uFF0C\u5176\u610F\u56FE\u5C31\u662F\u4E3A\u4E86\u4EE5\u4E00\u79CD\u76F4\u63A5\u4E86\u5F53\u548C\u9AD8\u6548\u7684\u65B9\u5F0F\u53BB\u64CD\u7EB5\u786C\u4EF6\u529F\u80FD\uFF0C\u800C\u4E0D\u987E\u5B89\u5168\u6027\u6216\u8005\u5BB9\u6613\u7406\u89E3\u8BF8\u65B9\u9762\u7684\u95EE\u9898\u3002\u5B83\u8FD8\u62E5\u6709\u4E00\u4E9B\u7279\u5F81\uFF0C\u4F7F\u5F97\u6211\u4EEC\u80FD\u591F\u5C06\u8FD9\u6837\u7684\u4EE3\u7801\u9690\u85CF\u5728\u4F18\u7F8E\u548C\u5B89\u5168\u7684\u754C\u9762\u4E4B\u540E\u3002</p><p>\u5F88\u81EA\u7136\uFF0C\u5BF9\u66F4\u5927\u578B\u7684\u7A0B\u5E8F\u4F7F\u7528C++\u8BED\u8A00\uFF0C\u5C06\u4F1A\u5BFC\u81F4\u7531\u6210\u7EC4\u7684\u7A0B\u5E8F\u5458\u4F7F\u7528C++\u3002C++\u6240\u5F3A\u8C03\u7684\u6A21\u5757\u5316\uFF0C\u5F3A\u7C7B\u578B\u7684\u754C\u9762\uFF0C\u4EE5\u53CA\u7075\u6D3B\u6027\u5728\u8FD9\u91CC\u90FD\u80FD\u53D1\u6325\u4F5C\u7528\u3002C++\u5728\u5404\u79CD\u4E3A\u5199\u5927\u7A0B\u5E8F\u800C\u63D0\u4F9B\u7684\u529F\u80FD\u4E4B\u95F4\u505A\u4E86\u5F88\u597D\u7684\u5E73\u8861\uFF0C\u50CF\u5176\u4ED6\u4EFB\u4F55\u8BED\u8A00\u4E00\u6837\u597D\u3002\u5F53\u7136\uFF0C\u968F\u7740\u7A0B\u5E8F\u53D8\u5F97\u66F4\u5927\uFF0C\u4E0E\u5B83\u4EEC\u7684\u5F00\u53D1\u548C\u7EF4\u62A4\u76F8\u5173\u7684\u95EE\u9898\u4E5F\u4F1A\u9010\u6E10\u4ECE\u8BED\u8A00\u7684\u95EE\u9898\u8F6C\u79FB\u5230\u66F4\u4E3A\u5168\u5C40\u6027\u7684\u5DE5\u5177\u548C\u7BA1\u7406\u7684\u95EE\u9898\u3002\u7B2C\u56DB\u90E8\u5206\u5C06\u63A2\u8BA8\u8FD9\u65B9\u9762\u7684\u8BBA\u9898\u3002</p><p>\u672C\u4E66\u5F3A\u8C03\u7684\u662F\u4E3A\u63D0\u4F9B\u901A\u7528\u529F\u80FD\u3001\u666E\u904D\u6709\u7528\u7684\u7C7B\u578B\u3001\u5E93\u7B49\u7684\u5404\u79CD\u6280\u672F\u3002\u8FD9\u4E9B\u6280\u672F\u80FD\u4E3A\u5199\u5C0F\u7A0B\u5E8F\u7684\u7A0B\u5E8F\u5458\u670D\u52A1\uFF0C\u4E5F\u80FD\u4E3A\u5199\u5927\u7A0B\u5E8F\u7684\u7A0B\u5E8F\u5458\u670D\u52A1\u3002\u8FDB\u4E00\u6B65\u8BF4\uFF0C\u7531\u4E8E\u4EFB\u4F55\u975E\u5E73\u51E1\u7684\u7A0B\u5E8F\u90FD\u662F\u7531\u8BB8\u591A\u534A\u72EC\u7ACB\u7684\u90E8\u5206\u7EC4\u6210\uFF0C\u5199\u8FD9\u4E9B\u90E8\u5206\u7684\u6280\u672F\u5B9A\u80FD\u670D\u52A1\u4E8E\u5F00\u53D1\u6240\u6709\u5E94\u7528\u7684\u7A0B\u5E8F\u5458\u3002</p><p>\u4F60\u6216\u8BB8\u4F1A\u6000\u7591\uFF0C\u7528\u66F4\u7EC6\u8282\u7684\u7C7B\u578B\u7ED3\u6784\u53BB\u523B\u753B\u5927\u7A0B\u5E8F\uFF0C\u4F1A\u4E0D\u4F1A\u5BFC\u81F4\u66F4\u5927\u7684\u7A0B\u5E8F\u6B63\u6587\uFF1F\u5BF9\u4E8EC++\u800C\u8A00\u60C5\u51B5\u5E76\u4E0D\u662F\u8FD9\u6837\u3002\u4E00\u4E2A\u58F0\u660E\u4E86\u51FD\u6570\u53C2\u6570\u7C7B\u578B\u3001\u4F7F\u7528\u4E86\u7C7B\u7B49\u7684C++\u7A0B\u5E8F\uFF0C\u901A\u5E38\u6BD4\u6CA1\u6709\u4F7F\u7528\u8FD9\u4E9B\u529F\u80FD\u7684\u7B49\u4EF7C\u7A0B\u5E8F\u77ED\u4E00\u70B9\u3002\u5728\u90A3\u4E9B\u4F7F\u7528\u4E86\u5E93\u7684\u5730\u65B9\uFF0CC++\u7A0B\u5E8F\u5C31\u8981\u6BD4\u7B49\u4EF7\u7684C\u7A0B\u5E8F\u77ED\u5F97\u591A\u3002\u5F53\u7136\u4E86\uFF0C\u8FD9\u91CC\u8FD8\u5F97\u5047\u5B9A\u90A3\u4E2A\u529F\u80FD\u7B49\u4EF7\u7684C\u7A0B\u5E8F\u786E\u5B9E\u80FD\u6784\u9020\u51FA\u6765\u3002</p><h2 id="_1-3-2-\u54F2\u5B66\u6CE8\u8BB0" tabindex="-1"><a class="header-anchor" href="#_1-3-2-\u54F2\u5B66\u6CE8\u8BB0" aria-hidden="true">#</a> 1.3.2 \u54F2\u5B66\u6CE8\u8BB0</h2><p>\u4E00\u4E2A\u7A0B\u5E8F\u8BBE\u8BA1\u8BED\u8A00\u8981\u670D\u52A1\u4E8E\u4E24\u4E2A\u76F8\u4E92\u5173\u8054\u7684\u76EE\u7684\uFF1A\u5B83\u8981\u4E3A\u7A0B\u5E8F\u5458\u63D0\u4F9B\u4E00\u79CD\u63CF\u8FF0\u6240\u9700\u6267\u884C\u7684\u52A8\u4F5C\u7684\u8F7D\u4F53\uFF1B\u8FD8\u8981\u4E3A\u7A0B\u5E8F\u5458\u63D0\u4F9B\u4E00\u7EC4\u6982\u5FF5\uFF0C\u4F7F\u4ED6\u4EEC\u80FD\u5229\u7528\u8FD9\u4E9B\u6982\u5FF5\u53BB\u601D\u8003\u4EC0\u4E48\u4E1C\u897F\u662F\u80FD\u591F\u505A\u7684\u3002\u5728\u7406\u60F3\u7684\u60C5\u51B5\u4E0B\uFF0C\u7B2C\u4E00\u4E2A\u7528\u9014\u8981\u6C42\u4E00\u79CD\u201C\u5C3D\u53EF\u80FD\u63A5\u8FD1\u673A\u5668\u7684\u201D\u8BED\u8A00\uFF0C\u4EE5\u4F7F\u673A\u5668\u7684\u6240\u6709\u91CD\u8981\u65B9\u9762\uFF0C\u90FD\u80FD\u4EE5\u4E00\u79CD\u5BF9\u7A0B\u5E8F\u5458\u76F8\u5F53\u660E\u663E\u7684\u65B9\u5F0F\u7B80\u5355\u800C\u6709\u6548\u5730\u52A0\u4EE5\u5904\u7406\u3002C\u8BED\u8A00\u7684\u57FA\u672C\u8BBE\u8BA1\u5C31\u662F\u57FA\u4E8E\u8FD9\u4E00\u89C2\u70B9\u3002\u800C\u7B2C\u4E8C\u4E2A\u7528\u9014\u6240\u8981\u6C42\u7684\u7406\u60F3\u8BED\u8A00\u662F\u201C\u5C3D\u53EF\u80FD\u63A5\u8FD1\u9700\u8981\u89E3\u51B3\u7684\u95EE\u9898\u201D\uFF0C\u8FD9\u6837\u624D\u80FD\u4F7F\u89E3\u51B3\u65B9\u6848\u4E2D\u7684\u6982\u5FF5\u80FD\u591F\u76F4\u63A5\u800C\u7D27\u51D1\u5730\u8868\u8FBE\u51FA\u6765\u3002\u88AB\u52A0\u5165C\u8BED\u8A00\uFF0C\u4ECE\u800C\u5851\u9020\u51FAC++\u7684\u90A3\u4E9B\u6982\u5FF5\uFF0C\u4ECE\u6839\u672C\u4E0A\u8BF4\uFF0C\u5C31\u662F\u57FA\u4E8E\u8FD9\u4E2A\u89C2\u70B9\u8BBE\u8BA1\u7684\u3002</p><p>\u5728\u6211\u4EEC\u601D\u8003\uFF0F\u7F16\u7A0B\u6240\u7528\u7684\u8BED\u8A00\u548C\u6211\u4EEC\u80FD\u591F\u8BBE\u60F3\u7684\u95EE\u9898\u4E0E\u89E3\u4E4B\u95F4\u7684\u8054\u7CFB\u975E\u5E38\u7D27\u5BC6\u3002\u6B63\u662F\u7531\u4E8E\u8FD9\u4E2A\u56E0\u7D20\uFF0C\u4EE5\u907F\u514D\u7A0B\u5E8F\u5458\u72AF\u9519\u8BEF\u4E3A\u76EE\u7684\u800C\u5BF9\u8BED\u8A00\u7684\u7279\u5F81\u52A0\u4EE5\u9650\u5236\uFF0C\u8FD9\u4E00\u505A\u6CD5\u81F3\u5C11\u4E5F\u662F\u5F88\u5371\u9669\u7684\u3002\u5C31\u50CF\u81EA\u7136\u8BED\u8A00\u4E2D\u7684\u60C5\u51B5\uFF0C\u638C\u63E1\u81F3\u5C11\u4E24\u79CD\u8BED\u8A00\u5C31\u975E\u5E38\u6709\u4EF7\u503C\u3002\u4E00\u4E2A\u8BED\u8A00\u4E3A\u7A0B\u5E8F\u5458\u63D0\u4F9B\u4E86\u4E00\u7EC4\u6982\u5FF5\u5DE5\u5177\uFF1B\u5982\u679C\u5B83\u4EEC\u4E0D\u9002\u5408\u4E8E\u67D0\u4EF6\u5DE5\u4F5C\uFF0C\u7A0B\u5E8F\u5458\u5C06\u7B80\u5355\u5730\u653E\u5F03\u8FD9\u4E2A\u8BED\u8A00\u3002\u597D\u7684\u8BBE\u8BA1\u548C\u4E0D\u51FA\u73B0\u9519\u8BEF\u90FD\u4E0D\u80FD\u4EC5\u7531\u67D0\u4E9B\u8BED\u8A00\u7279\u5F81\u7684\u5B58\u5728\u6216\u8005\u4E0D\u5B58\u5728\u6765\u4FDD\u8BC1\u3002</p><p>\u7C7B\u578B\u7CFB\u7EDF\u5BF9\u4E8E\u5404\u79CD\u5404\u6837\u975E\u5E73\u51E1\u7684\u5DE5\u4F5C\u90FD\u7279\u522B\u6709\u5E2E\u52A9\u3002\u4E8B\u5B9E\u4E0A\uFF0CC++\u7684\u7C7B\u6982\u5FF5\u5DF2\u7ECF\u88AB\u8BC1\u660E\u662F\u4E00\u79CD\u6781\u4E3A\u5F3A\u6709\u529B\u7684\u6982\u5FF5\u5DE5\u5177\u3002</p>',20);function r(t,c){return a}var _=p(e,[["render",r]]);export{_ as default};
