import{d as C}from"./app.fce06a0c.js";import{_ as i}from"./plugin-vue_export-helper.21dcd24c.js";const e={},a=C('<h1 id="_1-6-c\u548Cc" tabindex="-1"><a class="header-anchor" href="#_1-6-c\u548Cc" aria-hidden="true">#</a> 1.6 C\u548CC++</h1><p>C\u88AB\u9009\u4F5CC++\u7684\u57FA\u7840\u8BED\u8A00\u662F\u56E0\u4E3A\u5B83\uFF1A</p><ol><li>\u662F\u901A\u7528\u7684\u3001\u7B80\u6D01\u7684\u3001\u76F8\u5BF9\u4F4E\u7EA7\u7684\u3002</li><li>\u9002\u5408\u7528\u4E8E\u5927\u90E8\u5206\u7CFB\u7EDF\u7A0B\u5E8F\u8BBE\u8BA1\u5DE5\u4F5C\u3002</li><li>\u53EF\u4EE5\u5728\u6BCF\u4E2A\u5730\u65B9\u7684\u4EFB\u4F55\u7CFB\u7EDF\u4E0A\u8FD0\u884C\u3002</li><li>\u9002\u5E94\u4E8EUNIX\u7A0B\u5E8F\u8BBE\u8BA1\u73AF\u5883\u3002</li></ol><p>C\u6709\u5B83\u7684\u95EE\u9898\uFF0C\u4F46\u4E00\u4E2A\u4ECE\u7A7A\u767D\u51FA\u53D1\u8BBE\u8BA1\u7684\u8BED\u8A00\u4E5F\u5FC5\u7136\u4F1A\u6709\u4E00\u4E9B\u95EE\u9898\uFF0C\u51B5\u4E14\u6211\u4EEC\u4E86\u89E3C\u7684\u95EE\u9898\u3002\u66F4\u91CD\u8981\u7684\u662F\uFF0C\u4ECEC\u51FA\u53D1\u5DF2\u7ECF\u4F7F\u201C\u5E26\u7C7B\u7684C\u201D\u6210\u4E86\u4E00\u4E2A\u6709\u7528\u7684\u5DE5\u5177\uFF08\u6216\u8BB8\u8FD8\u6BD4\u8F83\u7B28\u62D9\uFF09\uFF0C\u800C\u4E14\u53EA\u662F\u5728\u5F00\u59CB\u60F3\u5230\u8981\u5C06\u7C7B\u4F3CSimula\u7684\u7C7B\u52A0\u5230C\u4E0A\u4E4B\u540E\u6CA1\u51E0\u4E2A\u6708\u7684\u65F6\u95F4\u3002</p><p>\u968F\u7740C++\u7684\u4F7F\u7528\u53D8\u5F97\u66F4\u52A0\u5E7F\u6CDB\uFF0C\u4EE5\u53CA\u5B83\u6240\u63D0\u4F9B\u7684\u8986\u76D6\u548C\u8D85\u8D8AC\u7684\u529F\u80FD\u53D8\u5F97\u66F4\u52A0\u91CD\u8981\uFF0C\u5173\u4E8E\u662F\u5426\u8FD8\u5E94\u8BE5\u4FDD\u6301\u517C\u5BB9\u6027\u7684\u95EE\u9898\u53C8\u4E00\u518D\u5730\u88AB\u63D0\u51FA\u6765\u3002\u5F88\u6E05\u695A\uFF0C\u5982\u679C\u629B\u5F03C\u7684\u67D0\u4E9B\u4F20\u7EDF\u5C31\u53EF\u4EE5\u907F\u514D\u4E00\u4E9B\u95EE\u9898\uFF08\u53C2\u89C1\uFF0C\u4F8B\u5982[Sethi\uFF0C1981]\uFF09\u3002\u8FD9\u4E9B\u90FD\u6CA1\u6709\u505A\uFF0C\u56E0\u4E3A\uFF1A</p><ol><li>\u5B58\u5728\u7740\u6210\u767E\u4E07\u884C\u7684C\u4EE3\u7801\u53EF\u80FD\u4ECEC++\u4E2D\u83B7\u76CA\uFF0C\u5148\u51B3\u6761\u4EF6\u662F\u4E0D\u5FC5\u5C06\u5B83\u4EEC\u5B8C\u5168\u4ECEC\u91CD\u5199\u5199\u6210C++\u3002</li><li>\u5B58\u5728\u7740\u6210\u767E\u4E07\u884C\u7528C\u5199\u51FA\u7684\u5E93\u51FD\u6570\u548C\u529F\u80FD\u8F6F\u4EF6\u4EE3\u7801\u53EF\u4EE5\u4ECEC++\u7A0B\u5E8F\u91CC\u4F7F\u7528\uFF0C\u6216\u8005\u5728C++\u7A0B\u5E8F\u4E4B\u4E0A\u4F7F\u7528\uFF1B\u5148\u51B3\u6761\u4EF6\u662FC++\u80FD\u591F\u4E0EC\u8FDE\u63A5\u517C\u5BB9\uFF0C\u4E14\u5728\u8BED\u6CD5\u4E0A\u4E0EC\u7C7B\u4F3C\u3002</li><li>\u5B58\u5728\u7740\u6570\u4EE5\u5341\u4E07\u8BA1\u7684\u7A0B\u5E8F\u5458\u4E86\u89E3C\u8BED\u8A00\uFF0C\u53EA\u9700\u8981\u53BB\u5B66\u4E60C++\u65B0\u7279\u5F81\u7684\u4F7F\u7528\uFF0C\u800C\u4E0D\u60F3\u53BB\u91CD\u65B0\u5B66\u4E60\u57FA\u7840\u3002</li><li>C++\u548CC\u5C06\u5728\u8BB8\u591A\u5E74\u4E2D\u88AB\u540C\u4E00\u4E9B\u4EBA\u7528\u4E8E\u540C\u6837\u7684\u7CFB\u7EDF\uFF0C\u56E0\u6B64\u5176\u5DEE\u5F02\u5FC5\u987B\u6216\u8005\u5F88\u5C0F\uFF0C\u6216\u8005\u662F\u5F88\u5927\uFF0C\u4EE5\u6700\u5927\u9650\u5EA6\u5730\u51CF\u5C11\u9519\u8BEF\u548C\u6DF7\u4E71\u7684\u53D1\u751F\u3002</li></ol><p>C++\u7684\u5B9A\u4E49\u5DF2\u7ECF\u505A\u4E86\u8BB8\u591A\u4FEE\u8BA2\uFF0C\u4EE5\u4FDD\u8BC1\u4EFB\u4F55\u540C\u65F6\u5728C\u548CC++\u91CC\u5408\u6CD5\u7684\u7ED3\u6784\u5728\u4E24\u4E2A\u8BED\u8A00\u4E2D\u90FD\u5177\u6709\u540C\u6837\u7684\u610F\u4E49\uFF08\u9664\u4E86\u5C11\u91CF\u4F8B\u5916\uFF1BB.2\u8282\uFF09\u3002</p><p>C\u8BED\u8A00\u672C\u8EAB\u4E5F\u5728\u53D1\u5C55\u548C\u6F14\u5316\uFF0C\u90E8\u5206\u5730\u662F\u5728C++\u5F00\u53D1\u7684\u5F71\u54CD\u4E4B\u4E0B[Rosler\uFF0C1984]\u3002ANSI C\u6807\u51C6[C\uFF0C1990]\u5C31\u5305\u542B\u4E86\u4ECE\u201C\u5E26\u7C7B\u7684C\u201D\u501F\u53BB\u7684\u51FD\u6570\u58F0\u660E\u8BED\u6CD5\u3002\u501F\u9274\u662F\u53CC\u5411\u7684\uFF0C\u4F8B\u5982void*\u6307\u9488\u7C7B\u578B\u662F\u4E3AANSI C\u53D1\u660E\u7684\uFF0C\u4F46\u5374\u5728C++\u91CC\u7B2C\u4E00\u6B21\u5B9E\u73B0\u3002\u6B63\u5982\u672C\u4E66\u7684\u7B2C\u4E00\u7248\u6240\u5141\u8BFA\u7684\uFF0CC++\u7684\u5B9A\u4E49\u5DF2\u7ECF\u8FC7\u4FEE\u8BA2\uFF0C\u4EE5\u53BB\u6389\u65E0\u7F18\u65E0\u6545\u7684\u4E0D\u517C\u5BB9\u6027\u3002\u4ECA\u5929\u7684C++\u6BD4\u539F\u6765\u66F4\u52A0\u4E0EC\u517C\u5BB9\u4E86\u3002\u8FD9\u91CC\u7684\u60F3\u6CD5\u662F\u8BA9C++\u5C3D\u53EF\u80FD\u63A5\u8FD1ANSI C---\u4F46\u53C8\u4E0D\u8FC7\u4E8E\u63A5\u8FD1[Koenig\uFF0C1989]\u3002\u767E\u5206\u4E4B\u767E\u7684\u517C\u5BB9\u6027\u4ECE\u6765\u5C31\u4E0D\u662F\u76EE\u6807\uFF0C\u56E0\u4E3A\u8FD9\u5C06\u5371\u5BB3\u7C7B\u578B\u5B89\u5168\u6027\u4EE5\u53CA\u7528\u6237\u7C7B\u578B\u4E0E\u5185\u90E8\u7C7B\u578B\u7684\u5E73\u6ED1\u96C6\u6210\u3002</p><p>\u4E86\u89E3C\u5E76\u4E0D\u662F\u5B66\u4E60C++\u7684\u5148\u51B3\u6761\u4EF6\u3002\u5728C\u4E2D\u7F16\u7A0B\u5E8F\u88AB\u9F13\u52B1\u4F7F\u7528\u7684\u8BB8\u591A\u6280\u672F\u548C\u8BC0\u7A8D\u7531\u4E8EC++\u8BED\u8A00\u7684\u7279\u5F81\u800C\u53D8\u5F97\u591A\u4F59\u4E86\u3002\u4F8B\u5982\uFF0C\u663E\u5F0F\u7C7B\u578B\u8F6C\u6362\uFF08casting\uFF09\u5728C++\u91CC\u5C31\u6CA1\u6709\u5728C\u91CC\u90A3\u4E48\u9891\u7E41\uFF081.6.1\u8282\uFF09\u3002\u7136\u800C\uFF0C\u597D\u7684C\u7A0B\u5E8F\u503E\u5411\u4E8E\u4E5F\u662FC++\u7A0B\u5E8F\u3002\u4F8B\u5982\uFF0C\u5728Kernighan\u548CRitchie\u7684\u300AThe C Programming Language\u300B\uFF0C2nd Edition\uFF08\u300AC\u7A0B\u5E8F\u8BBE\u8BA1\u8BED\u8A00\u300B\u5DF2\u7531\u673A\u68B0\u5DE5\u4E1A\u51FA\u7248\u793E\u7FFB\u8BD1\u51FA\u7248\uFF09[Kernighan\uFF0C1988]\u91CC\u7684\u6BCF\u4E2A\u7A0B\u5E8F\u90FD\u662FC++\u7A0B\u5E8F\u3002\u4EFB\u4F55\u6709\u5173\u9759\u6001\u7C7B\u578B\u8BED\u8A00\u7684\u7ECF\u9A8C\u5BF9\u4E8E\u5B66\u4E60C++\u4E5F\u90FD\u80FD\u6709\u6240\u5E2E\u52A9\u3002</p><h2 id="_1-6-1-\u7ED9c\u7A0B\u5E8F\u5458\u7684\u5EFA\u8BAE" tabindex="-1"><a class="header-anchor" href="#_1-6-1-\u7ED9c\u7A0B\u5E8F\u5458\u7684\u5EFA\u8BAE" aria-hidden="true">#</a> 1.6.1 \u7ED9C\u7A0B\u5E8F\u5458\u7684\u5EFA\u8BAE</h2><p>\u4E00\u4E2A\u4EBA\u5BF9C\u4E86\u89E3\u5F97\u8D8A\u597D\uFF0C\u5728\u5199C++\u7A0B\u5E8F\u65F6\u5927\u6982\u5C31\u8D8A\u96BE\u907F\u514DC\u7684\u98CE\u683C\uFF0C\u5E76\u4F1A\u56E0\u6B64\u4E22\u6389C++\u7684\u67D0\u4E9B\u6F5C\u5728\u4F18\u52BF\u3002\u8BF7\u770B\u4E00\u770B\u9644\u5F55B\uFF0C\u90A3\u91CC\u63CF\u8FF0\u4E86C\u4E0EC++\u4E4B\u95F4\u7684\u5DEE\u5F02\u3002\u8FD9\u91CC\u662F\u51E0\u4E2A\u6709\u5173\u7684\u8981\u70B9\uFF0C\u5728\u8FD9\u4E9B\u5730\u65B9\u505A\u540C\u6837\u7684\u4E8B\u60C5\u65F6\uFF0C\u5728C++\u91CC\u5B58\u5728\u6BD4C\u66F4\u597D\u7684\u65B9\u5F0F\uFF1A</p><ol><li><p>\u5728C++\u91CC\u51E0\u4E4E\u4E0D\u9700\u8981\u7528\u5B8F\u3002\u7528const\u6216enum\u5B9A\u4E49\u660E\u663E\u7684\u5E38\u91CF\uFF0C\u7528inline\u907F\u514D\u51FD\u6570\u8C03\u7528\u7684\u989D\u5916\u5F00\u9500\uFF0C\u7528template\u53BB\u523B\u753B\u4E00\u65CF\u51FD\u6570\u6216\u8005\u7C7B\u578B\uFF0C\u7528namespace\u53BB\u907F\u514D\u540D\u5B57\u51B2\u7A81\u3002</p></li><li><p>\u4E0D\u8981\u5728\u4F60\u9700\u8981\u53D8\u91CF\u4E4B\u524D\u53BB\u58F0\u660E\u5B83\uFF0C\u4EE5\u4FDD\u8BC1\u4F60\u80FD\u7ACB\u5373\u5BF9\u5B83\u8FDB\u884C\u521D\u59CB\u5316\u3002\u58F0\u660E\u53EF\u4EE5\u51FA\u73B0\u5728\u80FD\u51FA\u73B0\u8BED\u53E5\u7684\u6240\u6709\u4F4D\u7F6E\u4E0A\uFF0C\u53EF\u4EE5\u51FA\u73B0\u5728for\u8BED\u53E5\u7684\u521D\u59CB\u5316\u90E8\u5206\uFF0C\u53EF\u4EE5\u51FA\u73B0\u5728\u6761\u4EF6\u4E2D\u3002</p></li><li><p>\u4E0D\u8981\u7528malloc()\u3002new\u8FD0\u7B97\u7B26\u80FD\u5C06\u540C\u6837\u7684\u4E8B\u60C5\u505A\u5F97\u66F4\u597D\u3002\u5BF9\u4E8Erealloc()\uFF0C\u8BF7\u8BD5\u4E00\u8BD5vector()\u3002</p></li><li><p>\u8BD5\u7740\u53BB\u907F\u514Dvoid*\u3001\u6307\u9488\u7B97\u672F\u3001\u8054\u5408\u548C\u5F3A\u5236\uFF0C\u9664\u4E86\u5728\u67D0\u4E9B\u51FD\u6570\u6216\u7C7B\u5B9E\u73B0\u7684\u6DF1\u5C42\u4E4B\u5916\u3002\u5728\u5927\u90E8\u5206\u60C5\u51B5\u4E0B\uFF0C\u5F3A\u5236\u90FD\u662F\u8BBE\u8BA1\u9519\u8BEF\u7684\u6307\u793A\u5668\u3002\u5982\u679C\u4F60\u5FC5\u987B\u4F7F\u7528\u67D0\u4E2A\u663E\u5F0F\u7684\u7C7B\u578B\u8F6C\u6362\uFF0C\u8BF7\u8BBE\u6CD5\u53BB\u7528\u4E00\u4E2A\u201C\u65B0\u7684\u5F3A\u5236\u201D\uFF0C\u8BBE\u6CD5\u5199\u51FA\u4E00\u4E2A\u63CF\u8FF0\u4F60\u60F3\u505A\u7684\u4E8B\u60C5\u7684\u66F4\u7CBE\u786E\u7684\u8BED\u53E5\u3002</p></li><li><p>\u5C3D\u91CF\u5C11\u7528\u6570\u7EC4\u548CC\u98CE\u683C\u7684\u5B57\u7B26\u4E32\u3002\u4E0E\u4F20\u7EDF\u7684C\u98CE\u683C\u76F8\u6BD4\uFF0C\u4F7F\u7528C++\u6807\u51C6\u5E93string\u548Cvector\u5E38\u5E38\u53EF\u4EE5\u7B80\u5316\u7A0B\u5E8F\u8BBE\u8BA1\u3002</p></li></ol><p>\u5982\u679C\u8981\u7B26\u5408C\u7684\u8FDE\u63A5\u89C4\u5219\uFF0C\u4E00\u4E2AC++\u51FD\u6570\u5C31\u5FC5\u987B\u88AB\u58F0\u660E\u4E3A\u5177\u6709C\u8FDE\u63A5\u7684\u3002</p><p>\u6700\u91CD\u8981\u7684\u662F\uFF0C\u8BF7\u8BD5\u7740\u5C06\u7A0B\u5E8F\u8003\u8651\u4E3A\u4E00\u7EC4\u7531\u7C7B\u548C\u5BF9\u8C61\u8868\u793A\u7684\u76F8\u4E92\u4F5C\u7528\u7684\u6982\u5FF5\uFF0C\u800C\u4E0D\u662F\u4E00\u5806\u6570\u636E\u7ED3\u6784\u548C\u4E00\u4E9B\u53BB\u62E8\u5F04\u6570\u636E\u7ED3\u6784\u4E2D\u7684\u4E8C\u8FDB\u5236\u4F4D\u7684\u51FD\u6570\u3002</p><h2 id="_1-6-2-\u7ED9c-\u7A0B\u5E8F\u5458\u7684\u5EFA\u8BAE" tabindex="-1"><a class="header-anchor" href="#_1-6-2-\u7ED9c-\u7A0B\u5E8F\u5458\u7684\u5EFA\u8BAE" aria-hidden="true">#</a> 1.6.2 \u7ED9C++\u7A0B\u5E8F\u5458\u7684\u5EFA\u8BAE</h2><p>\u5230\u4ECA\u5929\uFF0C\u8BB8\u591A\u4EBA\u4F7F\u7528C++\u5DF2\u7ECF\u5341\u51E0\u5E74\u4E86\u3002\u5927\u90E8\u5206\u4EBA\u662F\u5728\u67D0\u4E2A\u5355\u4E00\u7684\u73AF\u5883\u91CC\u4F7F\u7528C++\uFF0C\u5E76\u5DF2\u5B66\u4F1A\u4E86\u5728\u65E9\u671F\u7F16\u8BD1\u5668\u548C\u7B2C\u4E00\u4EE3\u7684\u5E93\u6240\u5F3A\u52A0\u7684\u675F\u7F1A\u4E4B\u4E0B\u751F\u5B58\u3002\u7ECF\u5E38\u53EF\u4EE5\u770B\u5230\u8FD9\u79CD\u60C5\u51B5\uFF0C\u4E00\u4E2A\u5F88\u6709\u7ECF\u9A8C\u7684C++\u7A0B\u5E8F\u5458\u4E0D\u4EC5\u5F88\u591A\u5E74\u6CA1\u6709\u6CE8\u610F\u5F15\u8FDB\u7684\u65B0\u7279\u5F81\uFF0C\u4E5F\u6CA1\u6709\u770B\u5230\u6709\u5173\u7279\u5F81\u4E4B\u95F4\u5173\u7CFB\u7684\u53D8\u5316\uFF0C\u800C\u8FD9\u4E9B\u60C5\u51B5\u5DF2\u7ECF\u4F7F\u4E00\u4E9B\u5168\u65B0\u7684\u7A0B\u5E8F\u8BBE\u8BA1\u6280\u672F\u53D8\u6210\u53EF\u884C\u7684\u4E1C\u897F\u4E86\u3002\u6362\u53E5\u8BDD\u8BF4\uFF0C\u4F60\u5728\u7B2C\u4E00\u6B21\u5B66\u4E60C++\u65F6\u6CA1\u6709\u60F3\u5230\u6216\u8005\u8BA4\u4E3A\u4E0D\u5B9E\u9645\u7684\u4E1C\u897F\uFF0C\u6216\u8BB8\u4ECA\u5929\u5DF2\u7ECF\u53D8\u6210\u4E00\u79CD\u9AD8\u660E\u7684\u65B9\u5F0F\u3002\u4F60\u53EA\u6709\u901A\u8FC7\u91CD\u65B0\u8003\u5BDF\u57FA\u7840\u7684\u4E1C\u897F\u624D\u80FD\u5F04\u6E05\u695A\u5B83\u4EEC\u3002</p><p>\u8BF7\u6309\u987A\u5E8F\u6D4F\u89C8\u5404\u7AE0\uFF0C\u5982\u679C\u4F60\u5DF2\u7ECF\u77E5\u9053\u4E86\u67D0\u4E00\u7AE0\u7684\u5185\u5BB9\uFF0C\u4F60\u53EF\u4EE5\u53EA\u7528\u51E0\u5206\u949F\u5C31\u7FFB\u8FC7\u53BB\uFF1B\u5982\u679C\u4F60\u8FD8\u4E0D\u77E5\u9053\u5176\u5185\u5BB9\uFF0C\u90A3\u4E48\u4F60\u4F1A\u5B66\u5230\u4E00\u4E9B\u4E0D\u66FE\u9884\u6599\u5230\u7684\u4E1C\u897F\u3002\u6211\u5728\u5199\u8FD9\u672C\u4E66\u65F6\u5C31\u5B66\u5230\u4E86\u4E0D\u5C11\u4E1C\u897F\uFF0C\u800C\u4E14\u6211\u6000\u7591\u4F1A\u6709\u54EA\u4E2AC++\u7A0B\u5E8F\u5458\u77E5\u9053\u8FD9\u91CC\u7ED9\u51FA\u7684\u6240\u6709\u7279\u5F81\u548C\u6280\u672F\u3002\u8FDB\u4E00\u6B65\u8BF4\uFF0C\u8981\u7528\u597D\u8FD9\u4E2A\u8BED\u8A00\uFF0C\u4F60\u9700\u8981\u4E00\u79CD\u89C2\u70B9\uFF0C\u4EE5\u4FBF\u7ED9\u8FD9\u7EC4\u7279\u5F81\u548C\u6280\u672F\u5E26\u6765\u4E00\u79CD\u79E9\u5E8F\u3002\u901A\u8FC7\u4E66\u4E2D\u7684\u7EC4\u7EC7\u7ED3\u6784\u548C\u5B9E\u4F8B\uFF0C\u672C\u4E66\u5C31\u63D0\u4F9B\u4E86\u4E00\u79CD\u8FD9\u6837\u7684\u89C2\u70B9\u3002</p>',17);function l(p,r){return a}var o=i(e,[["render",l]]);export{o as default};
